            var orgiginalBackground = Console.BackgroundColor;
            var orgiginalForegroun = Console.ForegroundColor;

            try
            {
                Console.SetWindowSize(160, 50);
                Console.BackgroundColor = ConsoleColor.White;
                Console.ForegroundColor = ConsoleColor.DarkMagenta;
                Console.WriteLine(OutLogo);
            }
            catch (Exception)
            {
#if DEBUG
                Console.WriteLine("Unable to adjust screen size... not printing logo.");
#endif
